import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Alert from './alert.mp3';
import { navigate } from '@reach/router';


const audio = new Audio(Alert);


class Helper extends Component {

  constructor() {
    super();
    this.state = {
      help: false,
      audio: 0
    };

    this.toggleSwitch = this.toggleSwitch.bind(this);
    this.updateAudio = this.updateAudio.bind(this);

  }


  toggleSwitch(event){
    event.preventDefault();
    this.setState({
        help: !this.state.help
    })
    if(this.state.help === true){
        audio.play();
    }
  }



  updateAudio(event){
    event.preventDefault();
    audio.play();
    this.setState({
       audio: this.state.audio + 1
    })
    console.log(this.state.audio);
    if(this.state.audio === 6){
        audio.pause();
        audio.currentTime = 0;
        this.setState({
            audio: 0
         })
    }
    }

silence(){
    audio.pause();
    audio.currentTime = 0;
    this.setState({
        audio: 0
    });

}

  componentDidMount(){

    audio.addEventListener("ended", this.updateAudio);
    this.setState({help:true})
   // if(audio.ended = true) console.log('fuck');
  }

  componentWillUnmount(){
    audio.removeEventListener("ended");
  }


  render() {
    const { lat, lng } = this.props;
    const {help} = this.state;


    return (
<div className="p-4">

{lat}{"  "}{lng}

{help === true ? 
<div className="d-flex bg-light h-50 w-100 p-4 border-top">
    <div className="align-self-center">
        <h4 className="text-center">On standby for overdose victims</h4>
        <FontAwesomeIcon icon="heart" size="1x" className="heart text-danger text-center w-100"/>
    </div>
</div>
:
<div className="d-flex bg-light h-50 w-100 p-4 border-top">
    <div className="align-self-center">
        <h4 className="text-center">A victim nearby is in need of help!</h4>
        <button className="btn btn-danger font-weight-bold text-uppercase w-100 p-4 mt-2" onClick={this.navigate}>Assist Victim</button>
        <button className="btn btn-dark font-weight-bold text-uppercase w-100 p-4 mt-2" onClick={this.silence}>Silence Alert</button>
    </div>
</div>
}
<button onClick={this.toggleSwitch} className="btn btn-secondary">Toggle</button>


</div>


    );
  } 
}

export default Helper;
