import React, {Component} from 'react';
//import { Router, navigate } from '@reach/router';
import Firebase from './Firebase';
import './App.css';

import Register from './Register';

class App extends Component {

  constructor() {
    super();
    this.state = {
      user: null
    };
  }

  componentDidMount(){
    console.log("mounted");





    var starCountRef = Firebase.database().ref('/users/33');
    starCountRef.on('value', snapshot => {
      var userName = snapshot.val().username;
      console.log("username is ", userName);
      this.setState({user:userName});
    });

  }

  writeUserData = (userId, name, email, imageUrl) =>{
    Firebase.database().ref('users/' + userId).set({
      username: name,
      email: email,
      profile_picture : imageUrl
    });
  };

  addMeeting = meetingName => {
    const ref = Firebase
      .database()
      .ref(`meetings/${this.state.user.uid}`);
    ref.push({ meetingName: meetingName });
  };


  render(){
    const db = Firebase.database();
    console.log(db);
    console.log('yes');    
    //writeUserData("33","kirk","kirkhoey@gmail.com","urlllaskdjf");
    //writeUserData("3342d","kirk","kirkhoey@gmail.com","urlllaskdjf");


  return (


<div>
    <Register writeUserData={this.writeUserData} userName={this.state.user}/>

</div>
  );
}
}

export default App;
