import React, { Component } from 'react';
//import GoogleMap from './GoogleMap';
import OfferHelp from './OfferHelp';
import posed from 'react-pose';


  const config = {
    search: {
      opacity: 0.5,
      scale: 1,
      rotate: "0deg"
    },
    on: {
      opacity: 1,
      rotate: "90deg",
      transition: {
        rotate: {
          loop: Infinity
        }
      }
    }
  };


const Box = posed.div(config);

class Helper extends Component {

  constructor() {
    super();
    this.state = {
      help: null,
      isVisible: true,
      hovered:false
    };


  }


  componentDidMount(){
    this.setState({help:1})

    setInterval(() => {
        this.setState({ isVisible: !this.state.isVisible });
      }, 1000);

  }

  handleMouseEnter = () => {
    this.setState({
      hovered: true
    });
  };

  handleMouseLeave = () => {
    this.setState({
      hovered: false
    });
  };

  render() {
    const { hovered } = this.state;
    const { lat, lng } = this.props;



    return (
<div className="p-4">

{lat}{"  "}{lng}

{this.state.help && (<OfferHelp />)}

<Box className="box" pose={hovered ? "on" : "search"} />

</div>

    );
  } 
}

export default Helper;
